sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail: function() // define the function
	{
		var userRec = new GlideRecord("sys_user"); // define the glide record based on user table

		// use the sysparm_userid passed in to get a specific record from the user table
		userRec.get(this.getParameter('sysparm_userID')); 
		var temp = userRec.getDisplayValue();
		gs.info('User ID passed in was ' + temp);

		// return the email address or other property if needed from the record retrieivesd
		return userRec.email + "";
	},
    type: 'GetEmailAddress'
});
